mapper.update:
update goods_info
goods_name=#{goods_name}, goods_subscribe_uprice=#{goods_subscribe_uprice}, goods_onsale_subscribe_uprice=#{goods_onsale_subscribe_uprice}, goods_buy_uprice=#{goods_buy_uprice}, goods_onsale_buy_uprice=#{goods_onsale_buy_uprice}, goods_palate=#{goods_palate}, goods_palate_description=#{goods_palate_description}, goods_type=#{goods_type}, goods_type_description=#{goods_type_description}, goods_description=#{goods_description}, goods_stock=#{goods_stock}, goods_state=#{goods_state}, goods_reserved=#{goods_reserved},
where goods_id=#{goods_id} and goods_uniq_id = #{goods_uniq_id}
